QVariant
BleauMassifModel::data(const QModelIndex & index, int role) const
{
  if (!index.isValid() || index.row() < 0)
    return QVariant();

  if (index.row() >= m_items.count()) {
    qWarning() << "BleauMassifModel: Index out of bound";
    return QVariant();
  }

  const Item & item = m_items[index.row()];
  switch (role) {
  case ID:
    return item->id();
  case COORDINATE:
    return QVariant::fromValue(item->coordinate());
  case NAME:
    return item->name();
  case ACCESS:
    return item->access();
  case ALTERNATIVE_NAME:
    return item->alternative_name();
  case CHAOS_TYPE:
    return item->chaos_type();
  case NOTE:
    return item->note();
  case PARCELLES:
    return item->parcelles();
  case RDV:
    return item->rdv();
  case SECTEUR:
    return item->secteur();
  case VELO:
    return item->velo();
  default:
    break;
  }

  return QVariant();
}

