QVariant
BleauCircuitModel::data(const QModelIndex & index, int role) const
{
  if (!index.isValid() || index.row() < 0)
    return QVariant();

  if (index.row() >= m_items.count()) {
    qWarning() << "BleauCircuitModel: Index out of bound";
    return QVariant();
  }

  const Item & item = m_items[index.row()];
  switch (role) {
  case ID:
    return item->id();
  case COORDINATE:
    return QVariant::fromValue(item->coordinate());
  case COLOUR:
    return item->colour();
  case CREATION_DATE:
    return item->creation_date();
  case GESTION:
    return item->gestion();
  case GRADE:
    return item->grade();
  case MASSIF_ID:
    return item->massif_id();
  case NOTE:
    return item->note();
  case NUMBER:
    return item->number();
  case OPENER:
    return item->opener();
  case REFECTION_DATE:
    return item->refection_date();
  case REFECTION_NOTE:
    return item->refection_note();
  case STATUS:
    return item->status();
  case TOPOS:
    return item->topos();
  default:
    break;
  }

  return QVariant();
}

