int
BleauBoulderModel::rowCount(const QModelIndex & parent) const
{
  Q_UNUSED(parent);
  return m_items.size();
}

QVariant
BleauBoulderModel::data(const QModelIndex & index, int role) const
{
  if (!index.isValid() || index.row() < 0)
    return QVariant();

  if (index.row() >= m_items.count()) {
    qWarning() << "BleauBoulderModel: Index out of bound";
    return QVariant();
  }

  const Item & item = m_items[index.row()];
  switch (role) {
  case ID:
    return item->id();
  case COORDINATE:
    return QVariant::fromValue(item->coordinate());
  case NAME:
    return item->name();
  case COMMENT:
    return item->comment();
  case GRADE:
    return item->grade();
  case NUMBER:
    return item->number();
  case CIRCUIT_ID:
    return item->circuit_id();
  default:
    break;
  }

  return QVariant();
}

